.protester p {
  margin-top: 5px;
  color: #fafafa;
  font-size: 12px;
  font-weight: bold; }

.current-user svg {
  fill: #cc0000; }

.shake-vertical {
  animation: shake-vertical 1s cubic-bezier(0.455, 0.03, 0.515, 0.955) both; }

.slide-in-right {
  animation: slide-in-right 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) both; }

