  /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */ }

button {
  font-family: 'Comfortaa', cursive;
  font-size: 14px;
  font-weight: bold;
  position: relative;
  top: 0;
  padding: 15px;
  border-radius: 5px;
  border: 1px solid #0291a7;
  cursor: pointer;
  box-shadow: 0px 5px 0px 0px #027b8e, 0 12px 5px #000;
  transition: all 0.1s ease-in-out;
  background-color: #cc0000;
  color: #fafafa; }

button:hover {
  top: 1px;
  box-shadow: 0px 4px 0px 0px #027b8e, 0 11px 5px #000; }

button:active {
  top: 10px;
  box-shadow: 0px 2px 0px 0px #027b8e, 0 9px 5px #000; }

button:focus {
  outline: 0; }

a {
  color: #0291a7;
  text-decoration: none; }
  a:focus, a:hover {
    text-decoration: underline; }

.app {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 10px; }

.stats {
